uint d[100000], prime_grp[500000], cnt, curr;
char buf[700000], *p = buf;

__attribute__((optimize("-Ofast")))
inline void read() {
    while(!isdigit(*p)) ++p;
    curr = *p++ ^ 48;
    while(isdigit(*p)) curr = curr * 10 + (*p++ ^ 48);
}

__attribute__((optimize("-Ofast")))
inline uint find(uint a) {
    if(!d[a]) return a;
    return d[a] = find(d[a]);
}

__attribute__((optimize("-Ofast")))
inline void join(uint i, uint f) {
    f = (f - 1) >> 1;
    if(prime_grp[f]) {
        i = find(i), f = find(prime_grp[f]);
        if(i != f) d[i] = f, --cnt;
    } else {
        prime_grp[f] = i;
    }
}

__attribute__((optimize("-Ofast")))
int main() {
    fread(buf, 1, 700000, stdin);
    read();
    uint n = cnt = curr, j, f;
    for(register uint i = 1; i <= n; ++i) {
        read();
        f = prime[j = 0];
        do {
            if(curr % f == 0) {
                join(i, f);
                do { curr /= f; } while(curr % f == 0);
            }
            if(++j == 168) break;
            f = prime[j];
        } while(f * f <= curr);
        if(curr != 1) join(i, curr);
    }
    ull ans = 0;
    __asm__("movq %1,%%rax\n mulq %2\n divq %3\n":"=d"(ans):"m"(a1[cnt % 512]),"m"(a2[(cnt >> 9) % 256]),"m"(mod):"%rax");
    ans -= 2;
    puts(ans ? "YES" : "NO");
    printf("%d", ans);
    return 0;
}
